ee:message.ee:set-payload:
%dw 2.0
output application/xml
ns soapenv http://schemas.xmlsoap.org/soap/envelope/
ns inv http://warehouse.legacy.com
---
{
    soapenv#Envelope: {
        soapenv#Body: {
            inv#StockRequest: {
                inv#ProductId: payload.productId,
                inv#RequestDate: now() as String {format: "yyyy-MM-dd"}
            }
        }
    }
}

%dw 2.0
output application/xml
ns soapenv http://schemas.xmlsoap.org/soap/envelope/
ns inv http://warehouse.legacy.com
---
{
    soapenv#Envelope: {
        soapenv#Body: {
            inv#StockResponse: {
                inv#ProductID: vars.requestedId,
                // DYNAMIC LOGIC: If ID is P-999, quantity is 0
                inv#Quantity: if (vars.requestedId == "P-999") 0 else 500,
                // DYNAMIC STATUS: If ID is P-999, status is OUT_OF_STOCK
                inv#Status: if (vars.requestedId == "P-999") "OUT_OF_STOCK" else "IN_STOCK"
            }
        }
    }
}
