ee:message.ee:set-payload:
%dw 2.0
output application/xml
ns soapenv http://schemas.xmlsoap.org/soap/envelope/
ns inv http://warehouse.legacy.com
---
{
    soapenv#Envelope: {
        soapenv#Body: {
            inv#StockRequest: {
                inv#ProductId: payload.productId,
                inv#RequestDate: now() as String {format: "yyyy-MM-dd"}
            }
        }
    }
}

%dw 2.0
output application/json
ns ns0 http://warehouse.legacy.com
---
{
    productId: payload.Envelope.Body.ns0#StockResponse.ns0#ProductID,
    quantity: payload.Envelope.Body.ns0#StockResponse.ns0#Quantity as Number,

    // DYNAMIC LOGIC: If ID is P-999, return OUT_OF_STOCK, otherwise read from XML
    status: if (vars.requestedId == "P-999") "OUT_OF_STOCK" 
            else payload.Envelope.Body.ns0#StockResponse.ns0#Status,

    source: "Legacy SOAP System"
}
